from collections import Counter


class Solution:
    def longestSubstring2(self, s: str, k: int) -> int:
        from collections import Counter
        if not s:
            return 0
        s_num = Counter(s)
        low_k = {key for key in s_num if s_num[key] < k}
        # print(low_k)
        left = 0
        right = 0
        n = len(s)
        res = 0
        while right < n:
            tmp = Counter()
            while right < n and s[right] not in low_k:
                tmp[s[right]] += 1
                right += 1
            print(right, tmp)
            while left < right:
                if min([i for i in tmp.values() if i > 0]) >= k:
                    res = max(res, right - left)
                    break
                tmp[s[left]] -= 1
                left += 1
                print(tmp)
            while right < n and s[right] in low_k:
                right += 1
            left = right
            break

        return res

    def longestSubstring1(self, s: str, k: int) -> int:
        # for c in set(s):
        #     if s.count(c) < k:
        #         return max(self.longestSubstring1(i, k) for i in s.split(c))
        # return len(s)
        if len(s) < k:
            return 0
        c = min(set(s), key=s.count)
        if s.count(c) >= k:
            return len(s)
        return max(self.longestSubstring1(i, k) for i in s.split(c))

    def longestSubstring2(self, s: str, k: int) -> int:
        n = len(s)

        def helper(s, k, i):
            left, right = 0, 0
            special = 0
            more_k_num = 0
            lookup = Counter()
            res = 0
            while right < n:
                lookup[s[right]] += 1
                if lookup[s[right]] == 1:
                    special += 1
                if lookup[s[right]] == k:
                    more_k_num += 1
                while special > i:
                    lookup[s[left]] -= 1
                    if lookup[s[left]] == 0:
                        special -= 1
                    if lookup[s[left]] == k - 1:
                        more_k_num -= 1
                    left += 1
                if special == more_k_num:
                    res = max(res, right - left + 1)
                right += 1
            return res

        return max(helper(s, k, i) for i in range(1, 27))

    def longestSubstring7(self, s: str, k: int) -> int:
        from collections import Counter
        c = Counter()
        n = len(s)
        prefix = [c.copy()]
        for i in range(n):
            c[s[i]] += 1
            prefix.append(c.copy())

        def check(tmp):
            for val in tmp.values():
                if val < k:
                    return False
            return True

        res = 0
        for i in range(n + 1):
            for j in range(i):
                if check(prefix[i] - prefix[j]):
                    res = max(res, i - j)
                    break
        return res

    def longestSubstring(self, s: str, k: int) -> int:
        from collections import defaultdict
        if not s: return 0
        n = len(s)
        max_num = len(set(s))

        # 当不同字符个数为num，最长字符为多少
        def cal(num):
            #
            left = 0
            # 目前不同字符的个数
            cur = 0
            # 大于等于k字符的个数
            ge_k = 0
            # 记录字符个数
            c = defaultdict(int)
            # 最长字符串
            res = 0
            for right in range(n):
                c[s[right]] += 1
                # 不同字符个数
                if c[s[right]] == 1:
                    cur += 1
                # 大于等于k个数
                if c[s[right]] == k:
                    ge_k += 1
                # 当字符串不同个数大于 num
                while cur > num:
                    if c[s[left]] == 1:
                        cur -= 1
                    if c[s[left]] == k:
                        ge_k -= 1
                    c[s[left]] -= 1
                    left += 1
                if ge_k == num:
                    res = max(res, right - left + 1)
            return res

        return max(cal(num) for num in range(1, max_num + 1))


