class Solution(object):
	def countBinarySubstrings(self, s):
		"""
		给定一个字符串 s，
		计算具有相同数量0和1的非空(连续)子字符串的数量，并且这些子字符串中的所有0和所有1都是组合在一起的。
		重复出现的子串要计算它们出现的次数。
		---
		输入: "00110011"
		输出: 6
		解释: 有6个子串具有相同数量的连续1和0：“0011”，“01”，“1100”，“10”，“0011” 和 “01”。
		请注意，一些重复出现的子串要计算它们出现的次数。
		另外，“00110011”不是有效的子串，因为所有的0（和1）没有组合在一起。
		--
		输入: "10101"
		输出: 4
		解释: 有4个子串：“10”，“01”，“10”，“01”，它们具有相同数量的连续1和0。
		:type s: str
		:rtype: int
		"""
		n = len(s)
		if n < 2:return 0
		res = 0
		for i in range(n):
			num_0 = 0
			num_1 = 0
			flag = 1
			if s[i] == "0":
				num_0 += 1
			else:
				num_1 += 1

			for j in range(i+1,n):
				if s[j] != s[j-1]:
					flag += 1
					if flag > 2:
						break
				if s[j] == "0":
					num_0 += 1
				else:
					num_1 += 1
				if num_0 == num_1:
					res += 1
		return res

	def countBinarySubstrings1(self, s):
		res = []
		n = len(s)
		if n < 2: return 0
		i = 0
		while i < n:
			temp = s[i]
			temp_num = 1
			# j = i
			while i < n-1 and s[i] == s[i+1]:
				i += 1
				temp_num += 1
			res.append(temp_num)
			i += 1
		return sum(map(min,zip(res,res[1:])))
a = Solution()
print(a.countBinarySubstrings1("00110011"))
print(a.countBinarySubstrings1("10101"))
print(a.countBinarySubstrings1(""))

