class Solution(object):
	def totalHammingDistance(self, nums):
		"""
		:type nums: List[int]
		:rtype: int
		"""
		n = len(nums)
		res = 0
		def helper(i,j):
			return bin(i^j).count("1")
		for i in range(n):
			for j in range(i+1,n):
				res += helper(nums[i],nums[j])
		return res

	def totalHammingDistance1(self, nums):
		# nums = list(map(lambda x :"0"*(32-len(bin(x)[2:]))+bin(x)[2:],nums))
		res = 0
		n = len(nums)
		for i in zip(*map(lambda x :"0"*(32-len(bin(x)[2:]))+bin(x)[2:],nums)):
			temp = i.count("0")
			res += temp*(n-temp)
		return res
