class Solution(object):
    def validPalindrome(self, s):
        """
        给定一个非空字符串 s，最多删除一个字符。判断是否能成为回文字符串。
        ---
        输入: "aba"
        输出: True
        ---
        输入: "abca"
        输出: True
        解释: 你可以删除c字符。
        :type s: str
        :rtype: bool
        """
        if not s:
            return False
        if s == s[::-1]:
            return True
        n = len(s)
        for i in range(n):
            temp = s[:i] + s[i + 1:]
            if temp == temp[::-1]:
                return True
        return False

    def validPalindrome1(self, s):
        if not s:
            return False
        n = len(s)
        i = 0
        while i < n//2 and s[i] == s[-(i+1)]:
            i += 1
        tmp = s[i:n-i]
        return tmp[1:] == tmp[1:][::-1] or tmp[:-1] == tmp[:-1][::-1]
a = Solution()
print(a.validPalindrome1(
    ""))
